import React from 'react';

function EsportizandoDescription(){

    const functionalities = ["audio and text pre-processing, using Python and SQL",
                "development of a pipeline to automatically sync audio and transcription (forced alignment)",
                "optimization of the forced alignment process, leading to a 50% reduction of lost audio segments",
                "generation of all kinds of statistical analysis about the collected data",
                "co-writing of the project’s article"
                ]

    const relevantLinks = [["Project's github", "https://github.com/fergorgs/ProjectEduApp"],
                     ["Related article", "https://github.com/fergorgs/CORAA-scripts"]
                    ]

    const functionalitiesList = functionalities.map((functionality) => {
        return (
            <div className="item-container">
                <p>•</p>
                <p className="description">{functionality}</p>
            </div>
        )
    });

    const relevantLinksList = relevantLinks.map((relevantLink) => {

        return (
            <div class="about-button long-button" onClick={() => {window.location.href = relevantLink[1]; }}>
                <h4>{relevantLink[0]}</h4>
            </div>
        )
    });

    return(
        <div class="project-description">
            <h2>Esportizando</h2>
            <h3>Software Developer - Campus Mobile</h3>
            <p>Esportizando is an app submitted to the Campus Mobile competition, whose goal is to promote 
                innovative solutions using mobile technology. Taking part in a team of three, our app’s goal 
                was to encourage sports practice in the big cities.</p>

            <p>Brazil is the 5th most sedentary country in the world. Furthermore, the immense majority of 
                those who do practice sports falls into one of three categories of sports: Futball, Walking 
                or running, and volleyball.</p>

            <p>With this in mind, our app’s goals were to present people with: new sports, places to practice 
            those sports, and companions to practice together. These goals led to our app’s main functionalities:</p>

            <p>Working with React Native and Google’s Firebase, I improved the app’s GUI, 
                tanana, and tested its efisnecy</p>

            <p>My main contribution was implementing a front-end engine that allowed for didactic content 
                to be remotely added. The teacher would use a web-based graphical interface to model the 
                content as needed, and this model was then fetched by the app and turned into app screens.</p>

            {functionalitiesList}

            <h3 class="relevant-links-title">Relevant Links:</h3>

            {relevantLinksList}
        </div>
    )
}

export default EsportizandoDescription;
