import React from 'react';
import "../styles/ProjectSection.css"

function ProjectSection(props) {

    const baseWidth = 500
    const leftPercent = props.leftPercent ? props.leftPercent*baseWidth : 0.5*baseWidth;
    const rightPercent = (1-leftPercent)*baseWidth

    return(
        <div class="project-section">
            <div class="project-section-column project-section-column-left" style={{width: leftPercent.toString + '%'}}>
                <div class="project-section-container" style={{width: baseWidth.toString() + 'px'}}>
                    {props.leftComponent}
                </div>
            </div>
            <div class="vl"></div>
            <div class="project-section-column">
                <div class="project-section-container" style={{width: baseWidth.toString() + '500px'}}>
                    {props.rightComponent}
                </div>
            </div>
        </div>
    );
}

export default ProjectSection;
