import React, { useState } from 'react';
import '../styles/BackDropImage.css'

function heightAnimation(startingHeight, targetHeight, heightSetter, time) {

    const diff = targetHeight - currentHeight;
    var currentHeight = startingHeight

    var count = 0
    while(currentHeight != targetHeight)
    {
        count++;
        if(count > 100)
            break;
        if(targetHeight > currentHeight)
        {
            heightSetter(currentHeight+1);
            currentHeight++;
        }

        else
        {
            heightSetter(currentHeight-1)
            currentHeight--;
        }
        setTimeout(function() {
            //your code to be executed after 1 second
          }, 100);
    }
}

function BackDropImage(){

    const [backDropHeight, setBackdropHeight] = useState(500);

    return (
        <div>
        <div id="backdrop-img" style={{height: backDropHeight}}></div>
        <button onClick={() => heightAnimation(backDropHeight, 100, setBackdropHeight, 10)}>Trigger</button>
        </div>
    );
}

export default BackDropImage;
