import React, { useState } from 'react';
import SlideRoutes from 'react-slide-routes';
import {
  BrowserRouter,
  Routes,
  Route,
  Navigate,
  useLocation
} from "react-router-dom";
import HomeScreen from './pages/HomeScreen'
import WorksScreen from './pages/WorksScreen'
import NavBar from './components/NavBar'
import BackDropImage from './components/BackDropImage'
import "./styles/App.css"

function App() {

  const [currentRoute, setCurrentRoute] = useState('/');
  // const { pathName } = useLocation();
  var pathName = ''

  return (
    <div id="app">
      <BrowserRouter>
        <NavBar toPaths={['/', '/work', '/projects', '/contact']}/>
        <SlideRoutes duration={500}>
        <BackDropImage pathName={useLocation()}/>
          <Route index element={<HomeScreen/>} />
          <Route path="work" element={<WorksScreen/>}/>
          <Route path="projects" element={<TempScreen title="Projects screen"/>}/>
          <Route path="contact" element={<TempScreen title="Contact screen"/>}/>
          <Route path="*" element={<Navigate to="/" replace />}/>
        </SlideRoutes>
      </BrowserRouter>
    </div>
  );
}

function TempScreen(props){

  return (
    <h1>This is the {props.title}</h1>
  )
}

export default App;
